import React, { Component } from 'react';
import axios from 'axios';
import Navbar from './Navbar';
import Repo from './Repo';


class App extends Component {
  constructor() {
    super();
    this.state = {
      repos: []
    };
    this.getRepo = this.getRepo.bind(this);
  }
  getRepo() {
    axios.get('https://api.github.com/search/repositories?q=language:dart&per_page=3')
      .then(({ data }) => this.setState({ repos: data }));
  }

  renderRepos = () => {
    const { repos } = this.state;
    return (
      <div className='row'>
        <div className='col-md-8'>
          {repos.items.map(repo => <Repo key={repo.name} repo={repo} />)}
        </div>
      </div>
    )
  }

  render() {
    console.log(this.state.repos.items);
    return (
      <div>
        <Navbar />
        <div className='container'>
          <div className='card card-body'>
            <h2>Faça a pesquisa de 3 repositórios das seguintes linguagens: </h2>
            <ul className='list-inline'>
              <li className='list-inline-item'>Dart</li>
              <li className='list-inline-item'>Kotlin</li>
              <li className='list-inline-item'>JavaScript</li>
              <li className='list-inline-item'>Ruby</li>
              <li className='list-inline-item'>Swift</li>
            </ul>
            <button onClick={this.getRepo} id='procurar' type='button' className='btn btn-info'>Pesquisar</button>
            <div>
              {this.state.repos.length !== 0 ? this.renderRepos() : null}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
