import React, { Component } from 'react';
import axios from 'axios';
import Navbar from './Navbar'


class App extends Component{
  constructor() {
    super();
    this.state = {
      github: {
        url: "https://api.github.com/users",
        client_id: "Iv1.26c5af2f03eec821",
        client_secret: "4827d801fb6256ef2aefd9cff348a6afe7c2ce7a",
        count: 10,
        sort: "created: asc"
      },
      user: [],
      repos: []
    };
  }
