import React, { Component } from 'react';
import axios from 'axios';
import Navbar from './Navbar';
import Repo from './Repo';


class App extends Component{
  constructor() {
    super();
    this.state = {
      github: {
        url: "https://api.github.com/users",
        client_id: "Iv1.74b4a1ab2ed60c9f",
        client_secret: "4827d801fb6256ef2aefd9cff348a6afe7c2ce7a",
        count: 10,
        sort: "created: asc"
      },
      user: [],
      repos: []
    };
  }

  getRepos = e => {
    const user = e.target.value;
    const { url, client_id, client_secret, count, sort } = this.state.github;

    axios.get('https://api.github.com/users/reactjs/repos?per_page=5')
    .then(({ data }) => this.setState({ user: data }));

    // axios.get(`${url}/${user}/repos?per_page=${count}&sort=${sort}&client_id=${client_id}&client_secret=${client_secret}`)
    // .then(({ data }) => this.setState({ repos: data }));
  };

  renderRepos = () => {
    const repos = this.state;
    return(
      <div className='row'>
        <div className='col-md-8'>
          {repos.map(repo => <Repo key={repo.name} repo={repo}/>)}
        </div>
      </div>
    )
  }

  render() {
    console.log(this.state.user);
    return (
      <div className='App'>
        <Navbar />
        <div className='container'>
          <div className='card card-body'>
            <h2>Faça a pesquisa dos 10 principais repositórios das seguintes linguagens: </h2>
            <ul className='list-inline'>
              <li className='list-inline-item'>Dart</li>
              <li className='list-inline-item'>Kotlin</li>
              <li className='list-inline-item'>JavaScript</li>
              <li className='list-inline-item'>Ruby</li>
              <li className='list-inline-item'>Swift</li>
            </ul>
            <input onChange={this.getRepos} id='search' type='text' className='form-control' required />
            <button id='procurar' type='button' className='btn btn-info'>Pesquisar</button>
          </div>
          {this.renderRepos}
        </div>
      </div>
    );
  }
}

export default App;
