import React, { Component } from 'react';
import Navbar from './Navbar';
import Repo from './Repo';


class App extends Component {

  async getRepo() {
    const urlDart = 'https://api.github.com/search/repositories?q=language:dart&per_page=3';
    const urlKotlin = 'https://api.github.com/search/repositories?q=language:kotlin&per_page=3';
    const urlJs = 'https://api.github.com/search/repositories?q=language:javascript&per_page=3';
    const urlRuby = 'https://api.github.com/search/repositories?q=language:ruby&per_page=3';
    const urlSwift = 'https://api.github.com/search/repositories?q=language:swift&per_page=3';

    const resDart = await fetch(urlDart);
    const resKotlin = await fetch(urlKotlin);
    const resJs = await fetch(urlJs);
    const resRuby = await fetch(urlRuby);
    const resSwift = await fetch(urlSwift);

    const resultDart = await resDart.json();
    const resultKotlin = await resKotlin.json();
    const resultJs = await resJs.json();
    const resultRuby = await resRuby.json();
    const resultSwift = await resSwift.json();

    resultDart.items.forEach(i => {
      console.log(i.full_name, i.forks);
    });
    resultKotlin.items.forEach(i => {
      console.log(i.full_name);
    });
    resultJs.items.forEach(i => {
      console.log(i.full_name);
    });
    resultRuby.items.forEach(i => {
      console.log(i.full_name);
    });
    resultSwift.items.forEach(i => {
      console.log(i.full_name);
    });
  }

  render() {
    return (
      <div>
        <Navbar />
        <div className='container'>
          <div className='card card-body'>
            <h2>Faça a pesquisa de 3 repositórios das seguintes linguagens: </h2>
            <ul className='list-inline'>
              <li className='list-inline-item'>Dart</li>
              <li className='list-inline-item'>Kotlin</li>
              <li className='list-inline-item'>JavaScript</li>
              <li className='list-inline-item'>Ruby</li>
              <li className='list-inline-item'>Swift</li>
            </ul>
            <button onClick={this.getRepo} id='procurar' type='button' className='btn btn-info'>Pesquisar</button>
          </div>
        </div>
        <div className='card card-body mb-2'>
          <div className='row'>
            <div className='col-md-6'>
              <a href='repo.html_url' target='_blank'>{repo.name}</a>
            </div>
            <div className='col-md-6'>
              <span className='badge badge-primary'>Stars: {repo.stargazers_count}</span>
              <span className='badge badge-secondary'>Watch: {repo.watchers_count}</span>
              <span className='badge badge-success'>Forks: {repo.forks_count}</span>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
